import { Component, TemplateRef, ViewChild } from '@angular/core';

import { NzCardComponent } from 'ng-zorro-antd/card';
import { NzTypographyComponent } from 'ng-zorro-antd/typography';

import { NxThoughtChainComponent, NxThoughtChainItem } from 'ng-zorro-x/thought-chain';

@Component({
  selector: 'nx-demo-thought-chain-collapsible',
  imports: [NxThoughtChainComponent, NzCardComponent, NzTypographyComponent],
  template: `
    <nz-card style="width: 500px">
      <nx-thought-chain [nxItems]="nxItems" [nxCollapsible]="nxCollapsible" />
    </nz-card>

    <ng-template #content>
      <article nz-typography>
        <p nz-typography>
          In the process of internal desktop applications development, many different design specs and implementations
          would be involved, which might cause designers and developers difficulties and duplication and reduce the
          efficiency of development.
        </p>
        <p nz-typography>
          After massive project practice and summaries, Ant Design, a design language for background applications, is
          refined by Ant UED Team, which aims to
          <span nz-typography>
            <strong>
              uniform the user interface specs for internal background projects, lower the unnecessary cost of design
              differences and implementation and liberate the resources of design and front-end development
            </strong>
          </span>
        </p>
      </article>
    </ng-template>
  `
})
export class NxDemoThoughtChainCollapsibleComponent {
  @ViewChild('content', { static: true }) contentElement!: TemplateRef<void>;
  readonly nxItems: NxThoughtChainItem[] = [
    {
      key: '1',
      title: 'Thought Chain Item Title',
      description: 'description',
      content: this.contentElement
    },
    {
      key: '2',
      title: 'Thought Chain Item Title',
      description: 'description',
      content: this.contentElement
    }
  ];
  readonly nxCollapsible = {
    expandedKeys: ['1'],
    onExpand: (expandedKeys: string[]) => {
      console.log(expandedKeys);
    }
  };
}
